    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        // Obtener el correo y la contraseña del formulario
        String correo = txtCorreo.getText();
        String contrasena = new String(txtContrasena.getPassword());

        // Validar que los campos no estén vacíos
        if (correo.isEmpty() || contrasena.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por favor, ingresa correo y contraseña", "Campos vacíos", JOptionPane.WARNING_MESSAGE);
            return; // Detener la operación si hay campos vacíos
        }

        // Lógica de autenticación
        if (authenticate(correo, contrasena)) {
            // Si la autenticación es exitosa:
            JOptionPane.showMessageDialog(this, "¡Login exitoso!");

            // Ejemplo de cómo abrir la ventana MenuPrincipal (ajústalo según tu implementación real)
            MenuPrincipal menuPrincipal = new MenuPrincipal(); // Crear instancia de tu ventana principal
            menuPrincipal.setVisible(true); // Mostrar la ventana principal

            // Cerrar la ventana de login
            this.dispose();
}
    }//GEN-LAST:event_btnLoginActionPerformed

