    private boolean authenticate(String correo, String contrasena) {
        // Ejemplo de usuarios predefinidos (puedes ajustar según tus necesidades)
        Usuario[] usuarios = {
            new Usuario(1, "Nombre1", "Apellido1", "usuario1@example.com", "contrasena1"),
            new Usuario(2, "Nombre2", "Apellido2", "usuario2@example.com", "contrasena2"),
            // Agregar más usuarios según sea necesario
        };

        // Validar las credenciales
        for (Usuario usuario : usuarios) {
            if (usuario.getCorreo().equals(correo) && usuario.getContrasena().equals(contrasena)) {
                return true; // Usuario autenticado correctamente
            }
        }

        return false; // Si ninguna combinación coincide, la autenticación falla
    }

